String[] testWords = ["paper111", "4string", "no5te", "mother6"]
String[] correctWords = ["paper 111", "4 string", "note", "mother 6"]
loopEnd = new Integer(testWords.size())
count = 0;

while(count < loopEnd) {

	//Set up testWord Property
	String testWord = testWords[count];
	testRunner.testCase.testSuite.project.setPropertyValue("wrongWord", testWord)

	//Set up correctWord Property
	String correctWord = correctWords[count];
	testRunner.testCase.testSuite.project.setPropertyValue("correctWord", correctWord)

	//Send testWord and check response (built-in assert)
	testRunner.runTestStepByName("digitError")

	count++
}

